import { useParams } from "react-router-dom";
import "./WSindex.scss";
import { EditorContainer } from "./editorContainer";
import { useState } from "react";

export const WorkspaceScreen = () => {
  const params = useParams();
  const { fileId, folderId } = params;
  const [input, setInput] = useState('');

  const [output, setOutput] = useState('');

  const importInput = (e) => {
    const file = e.target.files[0];
  };
  

  return (
    <div className="workspace-container">
      <div className="header-container">
        <img src="./BFlogo.png" className="logo" />
      </div>

      <div className="content-container">

        <div className="editor-container">
          <EditorContainer />
        </div>

        <div className="input-output-container">
          <div className="input-header">
            <b>Input:</b>
            <label htmlFor="input" className="icon-container">
              <span className="material-icons">cloud_upload</span>
              <b className="">Import Input</b>
            </label>
            <input type="file" id="input" style={{ display: "none" }} onChange={importInput}/>
          </div>
          <textarea value = {input}></textarea>
        </div>

        <div className="input-output-container">
          <div className="input-header">
            <b>Output:</b>
            <button className="icon-container">
              <span className="material-icons">cloud_download</span>
              <b>Export Output</b>
            </button>
          </div>
          <textarea readOnly value = {output}></textarea>
        </div>

      </div>
    </div>
  );
};
