import { useContext } from "react";
import { WorkspaceContext } from "../../../providers/workspaceProvider";
import "./index.scss";
import { modalConstants, ModalContext } from "../../../providers/modalProvider";

const Folder = ({ folderTitle, cards, id }) => {

  const {deleteFolder} = useContext(WorkspaceContext);
  const {openModal, setModalPayload} = useContext(ModalContext);

  const onDeleteFolder = () => {
    deleteFolder(id);

  };

  const onEditFolderTitle = () => {
    setModalPayload(id);
    openModal(modalConstants.UPDATE_FOLDER_TITLE);
  };
  

  return (
    <div className="folder-container">
      <div className="folder-header">
        <div className="folder-header-item">
          <span className="material-icons" style={{ color: "#FFCA29" }}>
            folder
          </span>
          <span>{folderTitle}</span>
        </div>

        <div className="folder-header-item">
          <span className="material-icons" onClick={onDeleteFolder}>delete</span>
          <span className="material-icons" onClick={onUpdateFolderTitle}>edit</span>
          
        </div>
      </div>

      <div className="cards-container">
        {cards?.map((file, index) => {
          return (
            <div className="card" key={index}>
              <img src="BFlogo.png" alt="Logo" />
              <div className="title-container">
                <span>{file?.title}</span>
                <span>Language: {file?.language}</span>
              </div>
              <div style={{ display: "flex", gap: "10px" }}>
                <span className="material-icons">delete</span>
                <span className="material-icons">edit</span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export const RightComponent = () => {
  const {folders} = useContext(WorkspaceContext);
  const modalFeatures = useContext(ModalContext);

  // const openCreateNewFolderModal = () => {
  //   modalFeatures.openModal(modalFeatures.CREATE_FOLDER);
  // }

  return (
    <div className="right-container">
      <div className="header">
        <div className="title">
          <span>My</span> Workspace
        </div>
        
      </div>
      {folders?.map((folder, index) => {
        return <Folder folderTitle={folder?.title} cards={folder?.files} key = {folder.id} id = {folder.id} />;
      })}
    </div>
  );
};
